import React, { useState } from "react";
import { ReactComponent as SearchIcon } from "../assets/svgs/Linear.svg";
import { ReactComponent as Login } from "../assets/svgs/Bold.svg";

const Header = () => {
  const [search, setsearch] = useState("");

  const searchHandler = (e) => {
    e.preventDefault();
    setsearch(e.target.value);
  };
  return (
    <div style={{ backgroundColor: "#E2E8F0" }} className="px-2 py-3">
      <div className="flex justify-between items-center w-full px-12">
        <div className="text-xl font-bold ml-2" style={{ color: "#4A5568" }}>
          ADOPTEV
        </div>
        <div className="flex justify-start bg-white items-center px-3 py-2">
          <div>
            <SearchIcon />
          </div>
          <input
            type="text"
            value={search}
            placeholder="Search Your Favourite EV"
            className="px-2 py-1 outline-none border-none text-sm w-80"
            onChange={searchHandler}
          />
          {search && <div className="cursor-pointer" onClick={() => setsearch("")}>X</div>}
        </div>
        <div className="flex justify-around items-center text-sm w-72">
          <div>Finance</div>
          <div>Partner with us</div>
          <div
            className="flex items-center px-4 py-2 text-white rounded"
            style={{ background: "#319795" }}
          >
            <div>
              <Login />
            </div>
            <button className="outline-none border-none cursor-pointer ml-2 font-semibold">
              Login
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
